import type { APIRoute } from "astro";
import fs from "node:fs/promises";
import fssync from "node:fs";
import path from "node:path";
import { createRequire } from "node:module";
const require = createRequire(import.meta.url);
const carbone = require("carbone");

async function ensureDir(p: string) {
  await fs.mkdir(p, { recursive: true });
}

export const GET: APIRoute = async ({ url }) => {
  const debug = url.searchParams.get("debug") === "1";

  try {
    // 1) Rutas candidatas para la plantilla (usa ruta de archivo, no Buffer)
    const candidates = [
      path.join(process.cwd(), "templates", "template.docx"),
      path.join(process.cwd(), "src", "templates", "template.docx"),
    ];

    let tplPath: string | null = null;
    for (const p of candidates) {
      if (fssync.existsSync(p)) { tplPath = p; break; }
    }
    if (!tplPath) {
      const msg = [
        "[docx-min] No se encontrÃ³ test.docx",
        `cwd: ${process.cwd()}`,
        "Probadas:",
        ...candidates.map(c => `- ${c}`),
      ].join("\n");
      return new Response(debug ? msg : "No se pudo leer la plantilla.", { status: 500 });
    }

    // 2) Temp dir dedicado (Windows puede fallar si no existe)
    const tempDir = path.join(process.cwd(), ".carbone-tmp");
    await ensureDir(tempDir);
    carbone.set({ tempPath: tempDir });

    // 3) Datos muy simples
    const data = {
  "id": 4242,
  "date": "2024-05-19T16:27:21Z",
  "color": "#A544C5",
  "colorLight": "#FFF8FF",
  "company": {
    "name": "Carbone",
    "address": "130 Avenue Moon",
    "postalCode": "75000",
    "logo": "data:image/svg+xml;base64,",
    "city": "Paris"
  },
  "customer": {
    "name": "Michael J. Fox",
    "address": "6834 Hollywood Blvd",
    "postalCode": "90028",
    "city": "Los Angeles"
  },
  "products": [
    {
      "name": "Hoverboard",
      "priceUnit": 199.15,
      "quantity": 10,
      "priceTotal": 1991.5,
      "barcode_ean13": "978020137963",
      "image": "data:image/jpeg;base64,"
    },
    {
      "name": "DeLorean DMC-12",
      "priceUnit": 24900,
      "quantity": 1,
      "priceTotal": 24900,
      "barcode_ean13": "176024135963",
      "image": "data:image/jpeg;base64,"
    },
    {
      "name": "Plutonium",
      "priceUnit": 24,
      "quantity": 10,
      "priceTotal": 240,
      "barcode_ean13": "156822185761",
      "image": "data:image/jpeg;base64,"
    },
    {
      "name": "Guitar Gibson ES-345",
      "priceUnit": 120,
      "quantity": 1,
      "priceTotal": 120,
      "barcode_ean13": "186862175361",
      "image": "data:image/jpeg;base64,"
    }
  ]
};

    // 4) Render SIN convertTo (debe devolver DOCX)
    const out: Buffer = await new Promise((resolve, reject) => {
      carbone.render(tplPath!, data, {}, (err: any, res: Buffer) => {
        if (err) return reject(err);
        resolve(res);
      });
    });

    if (!out || !out.length) {
      const msg = "[docx-min] Render devolviÃ³ resultado vacÃ­o";
      return new Response(debug ? msg : "No se pudo generar DOCX.", { status: 500 });
    }

    return new Response(out, {
      status: 200,
      headers: {
        "Content-Type":
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
        "Content-Disposition": 'attachment; filename="demo.docx"',
      },
    });
  } catch (e: any) {
    const msg = `[docx-min] Error: ${e?.stack || e?.message || String(e)}`;
    return new Response(debug ? msg : "No se pudo generar DOCX.", { status: 500 });
  }
};
